// Reading user input from the text file
var fs = require('fs');
var data = fs.readFileSync('/var/tmp/Roost/RoostGPT/postman-test/94cc83b0-bd25-4701-be5a-cf7d15f7e7a6/roost_user_input.txt', 'utf8');
var userInputs = data.split('\n');

// Iterating through each user input and performing the test
for (let i = 0; i < userInputs.length; i++) {
    let userInput = userInputs[i];

    // Test 1: Check if the response status code is equal to the user input
    pm.test("Status code is " + userInput, function () {
        pm.response.to.have.status(userInput);
    });

    // Test 2: Check if the response time is less than or equal to the user input
    pm.test("Response time is less than or equal to " + userInput, function () {
        pm.expect(pm.response.responseTime).to.be.at.most(userInput);
    });

    // Test 3: Check if the response body contains the user input
    pm.test("Body contains " + userInput, function () {
        pm.expect(pm.response.text()).to.include(userInput);
    });

    // Test 4: Check if the response header contains the user input
    pm.test("Header contains " + userInput, function () {
        pm.expect(pm.response.headers).to.have.property(userInput);
    });
}

