    pm.expect(jsonData).to.have.property("kind");
    pm.expect(jsonData.kind).to.eql(input.kind);

    // Validate the `data` field
    pm.expect(jsonData).to.have.property("data");
    pm.expect(jsonData.data).to.eql(input.data);
});

