const fs = require('fs');

// Read the user input file
const user_input_file = '/var/tmp/Roost/RoostGPT/postman-test/94cc83b0-bd25-4701-be5a-cf7d15f7e7a6/roost_user_input.txt';
const user_input = fs.readFileSync(user_input_file, 'utf8');

// Parse user input into JSON
const user_data = JSON.parse(user_input);

// Define individual tests based on user data
user_data.forEach((data, index) => {

  // Define the test name
  const test_name = `Test ${index + 1}: ${data.test_name}`;

  // Define the test function
  const test_function = () => {

    // Set the request URL and method based on user data
    pm.request.url = data.url;
    pm.request.method = data.method;

    // Set the request headers and body based on user data
    pm.request.headers = data.headers;
    pm.request.body = data.body;

    // Send the request and validate the response
    pm.sendRequest(pm.request, (error, response) => {

      // Check if there was an error
      if (error) {
        console.log(`Error: ${error}`);
        return;
      }

      // Validate the response status
      pm.test(`Response status is ${data.expected_status}`, () => {
        pm.expect(response.status).to.equal(data.expected_status);
      });

      // Validate the response body
      pm.test(`Response body is as expected`, () => {
        pm.expect(response.json()).to.deep.equal(data.expected_body);
      });

    });
  };

  // Add the test to the Postman collection
  pm.collection.addTest(test_name, test_function);
});

