// Load user input
var fs = require('fs');
var path = "/var/tmp/Roost/RoostGPT/postman-test/94cc83b0-bd25-4701-be5a-cf7d15f7e7a6/roost_user_input.txt";
var userInput = fs.readFileSync(path, 'utf8');

// Parse user input
var input = JSON.parse(userInput);

// Compare the statusCode variable with the response code for assertion
pm.expect(parseInt(pm.variables.get("statusCode"))).to.be.equal(pm.response.code);

// Check for response code and perform respective tests
if (pm.response.code == 200){
    pm.test("The consumer.", function () {
      pm.expect(pm.response.status).to.be.eql("OK");

      // Iterate through each field in user input and check for its presence in response
      for(var field in input){
          pm.test('Response has the `' + field + '` field',function(){
            const responseData = pm.response.json();
            if(pm.expect(responseData).to.have.nested.property(field)){
              pm.expect(typeof _.get(pm.response.json(), field, "")).to.equal(typeof input[field]);
            }
        })  
      }
    });
} 
else if (pm.response.code == 400 || pm.response.code == 401 || pm.response.code == 403){
    // Perform common error tests for 400, 401, 403
    pm.test("Error response validation", function () {
      pm.expect(pm.response.status).to.not.be.eql("OK");

      // Iterate through each field in user input and check for its absence in response
      for(var field in input){
          pm.test('Response should not have the `' + field + '` field',function(){
            const responseData = pm.response.json();
            pm.expect(responseData).to.not.have.nested.property(field);
        })  
      }
    });
} 
else if (pm.response.code == 429){
    pm.test("Rate limit reached", function () {
      pm.expect(pm.response.status).to.be.eql("Too Many Requests");
    });
}
