const fs = require('fs');
const userInputPath = "/var/tmp/Roost/RoostGPT/postman-test/c7e893fb-e92e-4bde-b7d2-009b9d6d918f/roost_user_input.txt";
let rawUserInput = fs.readFileSync(userInputPath);
let userInput = JSON.parse(rawUserInput);

// Use userInput in your tests
pm.test("User Input Test", function () {
  const responseJson = pm.response.json();

  // Loop through all key-value pairs in userInput
  for (let key in userInput) {
    // Check if the response has the key from userInput
    pm.test(`Response has the ${key} field`, function () {
      pm.expect(responseJson).to.have.property(key);
    });

    // Check if the value of the key in the response matches the value in userInput
    pm.test(`Response's ${key} value matches userInput`, function () {
      pm.expect(responseJson[key]).to.eql(userInput[key]);
    });
  }
});

