// Import user inputs from external file
const fs = require('fs');
const userInput = fs.readFileSync('/var/tmp/Roost/RoostGPT/postman-test/c7e893fb-e92e-4bde-b7d2-009b9d6d918f/roost_user_input.txt', 'utf8');

// Parse user inputs into JSON
const userInputs = JSON.parse(userInput);

// Compare the statusCode variable with the response code for assertion
pm.expect(parseInt(pm.variables.get("statusCode"))).to.be.equal(pm.response.code);

if (pm.response.code == 200){
    pm.test("API Key.", function () {
      pm.expect(pm.response.status).to.be.eql("OK");

        pm.test('Response has the `api_version` field',function(){
            const responseData = pm.response.json();
            if(pm.expect(responseData).to.have.nested.property("api_version")){
              pm.expect(typeof _.get(pm.response.json(), "api_version", "")).to.equal("string");
            }
        })  

          // Validate api_version with user input
          pm.expect(_.get(pm.response.json(), "api_version", "")).to.be.oneOf(userInputs.api_versions);

        pm.test('Response has the `kind` field',function(){
            const responseData = pm.response.json();
            if(pm.expect(responseData).to.have.nested.property("kind")){
              pm.expect(typeof _.get(pm.response.json(), "kind", "")).to.equal("string");
            }
        })  

          // Validate kind with user input
          pm.expect(_.get(pm.response.json(), "kind", "")).to.be.oneOf(userInputs.kinds);

        pm.test('Response has the `data` field',function(){
            const responseData = pm.response.json();
            if(pm.expect(responseData).to.have.nested.property("data")){
              pm.expect(Array.isArray(_.get(pm.response.json(), "data"))).to.equal(true);
            }
        })  

        pm.test('Each object in the `data` field has all the required fields',function(){         
          pm.expect(pm.response.json()).to.have.nested.property("data");  
          let dataArray = _.get(pm.response.json(), "data", "");  
          for (let i = 0; i < dataArray.length; i++) {  

        if(pm.expect(pm.response.json().data[i]).to.have.nested.property("spec")){
          pm.expect(typeof _.get(pm.response.json(), `data[${i}].spec`, "")).to.equal("object");
        } 

          }

      })
});
}

