const fs = require('fs');
let rawdata = fs.readFileSync('/var/tmp/Roost/RoostGPT/postman-test/c7e893fb-e92e-4bde-b7d2-009b9d6d918f/roost_user_input.txt');
let userInput = JSON.parse(rawdata);

// Compare the statusCode variable with the response code for assertion
pm.expect(parseInt(userInput.statusCode)).to.be.equal(pm.response.code);

if (pm.response.code == 204){
    pm.test("An API Key is being deleted.", function () {
      pm.expect(pm.response.status).to.be.eql("No Content");
    });
}

if (pm.response.code == 400){
    pm.test("Bad Request", function () {
      pm.expect(pm.response.status).to.be.eql("Bad Request");
    });
    // Check if each error object has all required fields
    pm.test('Each object in the `errors` field has all the required fields', function() {
        const responseData = pm.response.json();
        pm.expect(responseData).to.have.property("errors");
        const errors = responseData.errors;
        errors.forEach(error => {
            pm.expect(error).to.have.property("id");
            pm.expect(error).to.have.property("status");
            pm.expect(error).to.have.property("code");
            pm.expect(error).to.have.property("title");
            pm.expect(error).to.have.property("detail");
            pm.expect(error).to.have.property("source");
            pm.expect(error).to.have.property("error_code");
            pm.expect(error).to.have.property("message");
        });
    });
}

if (pm.response.code == 401){
    pm.test("The request lacks valid authentication credentials for this resource.", function () {
      pm.expect(pm.response.status).to.be.eql("Unauthorized");
    });
}

// Continue with similar condition checks for other status codes...

// Check if the response time is below the user defined response time
pm.test("Response time is less than " + userInput.responseTime, function () {
    pm.expect(pm.response.responseTime).to.be.below(userInput.responseTime);
});

