pm.expect(parseInt(pm.variables.get("statusCode"))).to.be.equal(pm.response.code);

// Test based on response status code
switch (pm.response.code) {
  case 200:
    pm.test("The topic.", function () {
      pm.expect(pm.response.status).to.be.eql("OK");

      // Iterate over user input parameters and check if they exist in the response
      for (let param of inputParams) {
        pm.test(`Response has the \`${param}\` field`, function(){
          const responseData = pm.response.json();
          if(pm.expect(responseData).to.have.nested.property(param)){
            pm.expect(typeof _.get(responseData, param, "")).to.be.a('string');
          }
        })  
      }
    });
    break;

  case 400:
    pm.test("Indicates a bad request error. It could be caused by an unexpected request body format or other forms of request validation failure.", function () {
      pm.expect(pm.response.status).to.be.eql("Bad Request");

      // Check if error details exist in the response
      pm.test('Response has the `detail` field',function(){
        const responseData = pm.response.json();
        if(pm.expect(responseData).to.have.nested.property("detail")){
          pm.expect(typeof _.get(pm.response.json(), "detail", "")).to.equal("string");
        }
      })  
    });
    break;

  case 401:
    pm.test("Indicates a client authentication error. Kafka authentication failures will contain error code 40101 in the response body.", function () {
      pm.expect(pm.response.status).to.be.eql("Unauthorized");

      // Check if error details exist in the response
      pm.test('Response has the `detail` field',function(){
        const responseData = pm.response.json();
        if(pm.expect(responseData).to.have.nested.property("detail")){
          pm.expect(typeof _.get(pm.response.json(), "detail", "")).to.equal("string");
        }
      })  
    });
    break;

  case 403:
    pm.test("Indicates a client authorization error. Kafka authorization failures will contain error code 40301 in the response body.", function () {
      pm.expect(pm.response.status).to.be.eql("Forbidden");

      // Check if error details exist in the response
      pm.test('Response has the `detail` field',function(){
        const responseData = pm.response.json();
        if(pm.expect(responseData).to.have.nested.property("detail")){
          pm.expect(typeof _.get(pm.response.json(), "detail", "")).to.equal("string");
        }
      })  
    });
    break;

  case 429:
    pm.test("Indicates that a rate limit threshold has been reached, and the client should retry again later.", function () {
      pm.expect(pm.response.status).to.be.eql("Too Many Requests");
    });
    break;

  case 500:
    pm.test("A server-side problem that might not be addressable from the client side. Retriable Kafka errors will contain error code 50003 in the response body.", function () {
      pm.expect(pm.response.status).to.be.eql("Internal Server Error");
    });
    break;
}

