let responseData;
const contentType = pm.response.headers.get('Content-Type');
if(contentType === "application/xml"){
    responseData = xml2Json(responseBody);
} else{
    responseData = pm.response.json();
}

if (pm.variables.get("statusCode")) {
    pm.expect(parseInt(pm.variables.get("statusCode"))).to.be.equal(pm.response.code);
}

if (pm.response.code == 200){
    pm.test("API Key.", function () {
        pm.expect(pm.response.status).to.be.eql("OK");
        pm.expect(responseData).to.have.nested.property("api_version");
        pm.expect(responseData.api_version).to.be.a("string");
        pm.expect(responseData.api_version).to.include.oneOf(["iam/v2"]);
        pm.expect(responseData).to.have.nested.property("kind");
        pm.expect(responseData.kind).to.be.a("string");
        pm.expect(responseData.kind).to.include.oneOf(["ApiKeyList"]);
        pm.expect(responseData).to.have.nested.property("data");
        pm.expect(responseData.data).to.be.an("array");
        pm.expect(_.uniq(responseData.data).length).to.be.eql(responseData.data.length);

        responseData.data.forEach((item, index) => {
            pm.test(`Data item at index ${index} has a 'spec' property of type object`, function() {
                pm.expect(item).to.have.property("spec").that.is.an("object");
            });
        });
    });
}

if (pm.response.code == 400){
    pm.test("Bad Request", function () {
        pm.expect(pm.response.status).to.be.eql("Bad Request");
        pm.expect(responseData).to.have.nested.property("errors");
        pm.expect(responseData.errors).to.be.an("array");
        pm.expect(_.uniq(responseData.errors).length).to.be.eql(responseData.errors.length);

        responseData.errors.forEach((error, index) => {
            pm.test(`Error item at index ${index} has required properties`, function() {
                pm.expect(error).to.have.property("id").that.is.a("string");
                pm.expect(error).to.have.property("status").that.is.a("string");
                pm.expect(error).to.have.property("code").that.is.a("string");
                pm.expect(error).to.have.property("title").that.is.a("string");
                pm.expect(error).to.have.property("detail").that.is.a("string");
                pm.expect(error).to.have.property("source").that.is.an("object");
                pm.expect(error).to.have.property("error_code").that.is.a("number");
                pm.expect(error).to.have.property("message").that.is.a("string");
            });
        });
    });
}

if (pm.response.code == 401){
    pm.test("Unauthorized", function () {
        pm.expect(pm.response.status).to.be.eql("Unauthorized");
        // Similar tests as for 400 Bad Request can be replicated here for 401 Unauthorized
    });
}

if (pm.response.code == 403){
    pm.test("Forbidden", function () {
        pm.expect(pm.response.status).to.be.eql("Forbidden");
        // Similar tests as for 400 Bad Request can be replicated here for 403 Forbidden
    });
}

if (pm.response.code == 429){
    pm.test("Too Many Requests", function () {
        pm.expect(pm.response.status).to.be.eql("Too Many Requests");
    });
}

if (pm.response.code == 500){
    pm.test("Internal Server Error", function () {
        pm.expect(pm.response.status).to.be.eql("Internal Server Error");
        // Similar tests as for 400 Bad Request can be replicated here for 500 Internal Server Error
    });
}

