let responseData;
const contentType = pm.response.headers.get('Content-Type');
if (contentType === "application/xml") {
    responseData = xml2Json(responseBody);
} else {
    responseData = pm.response.json();
}

if (pm.variables.get("statusCode")) {
    pm.expect(parseInt(pm.variables.get("statusCode"))).to.be.equal(pm.response.code);
}

if (pm.response.code === 202) {
    pm.test("An API Key is being created.", function () {
        pm.expect(pm.response.status).to.be.eql("Accepted");
        pm.expect(responseData).to.have.nested.property("api_version");
        pm.expect(responseData).to.have.nested.property("kind");
        pm.expect(responseData).to.have.nested.property("id");
        pm.expect(responseData).to.have.nested.property("spec");
        pm.expect(responseData.api_version).to.be.oneOf(["iam/v2"]);
        pm.expect(responseData.kind).to.be.oneOf(["ApiKey"]);
        pm.expect(responseData.id).to.be.a("string").and.to.have.lengthOf.at.most(255);
        pm.expect(responseData.spec).to.be.an("object");
        pm.expect(responseData.spec.secret).to.be.a("string");
        pm.expect(responseData.spec.display_name).to.be.a("string");
        pm.expect(responseData.spec.description).to.be.a("string");
    });
}

const checkErrorResponse = (errorCode, statusMessage) => {
    pm.test(`${errorCode} - ${statusMessage}`, function () {
        pm.expect(pm.response.status).to.be.eql(statusMessage);
        pm.expect(responseData).to.have.nested.property("errors");
        pm.expect(responseData.errors).to.be.an("array").and.to.have.unique;
        responseData.errors.forEach((error, index) => {
            pm.expect(error).to.have.all.keys('id', 'status', 'code', 'title', 'detail', 'source', 'error_code', 'message');
            pm.expect(error.id).to.be.a("string");
            pm.expect(error.status).to.be.a("string");
            pm.expect(error.code).to.be.a("string");
            pm.expect(error.title).to.be.a("string");
            pm.expect(error.detail).to.be.a("string");
            pm.expect(error.source).to.be.an("object");
            pm.expect(error.error_code).to.be.a("number");
            pm.expect(error.message).to.be.a("string");
        });
    });
};

const errorResponseCodes = {
    "400": "Bad Request",
    "401": "Unauthorized",
    "402": "Payment Required",
    "403": "Forbidden",
    "409": "Conflict",
    "422": "Unprocessable Entity (WebDAV) (RFC 4918)",
    "429": "Too Many Requests",
    "500": "Internal Server Error"
};

Object.keys(errorResponseCodes).forEach((code) => {
    if (pm.response.code.toString() === code) {
        checkErrorResponse(code, errorResponseCodes[code]);
    }
});

