function commonErrorResponseTests(data) {
    const fields = ["id", "status", "code", "title", "detail", "source", "message"];
    fields.forEach(field => {
        pm.test(`Response has the \`${field}\` field`, function () {
            if (pm.expect(data).to.have.nested.property(field)) {
                const fieldType = typeof _.get(data, field, "");
                if (data[field] != null) {
                    pm.expect(fieldType).to.be.oneOf(["string", "object", "number"]);
                }
            }
        });
    });

    pm.test('id length to be at most `255`', function () {
        pm.expect(_.get(data, "id", "").length).to.be.at.most(255);
    });

    if (data.source) {
        pm.test('Response has the `source.pointer` field', function () {
            if (pm.expect(data.source).to.have.property("pointer")) {
                pm.expect(typeof data.source.pointer).to.equal("string");
            }
        });

        pm.test('Response has the `source.parameter` field', function () {
            if (pm.expect(data.source).to.have.property("parameter")) {
                pm.expect(typeof data.source.parameter).to.equal("string");
            }
        });
    }

    pm.test('Response has the `error_code` field', function () {
        if (pm.expect(data).to.have.nested.property("error_code")) {
            if (data.error_code != null) {
                pm.expect(typeof data.error_code).to.equal("number");
            }
        }
    });
}

